	long start = System.nanoTime();
	if (vel.intValue()>0) {
		pit-=36;
		scales.setFreqBase(new Float (20));
		Float f = scales.getNoteFreq(pit);
		//LogWindow.log("note:"+pit+"="+f.floatValue(),"warn");
		int index=(poly.intValue()+1);
		Vector v = new Vector();
