		t.oscUtil.simpleOscMessage("/bird"+index+"/f",v,0);
		Vector v1 = new Vector();v1.add(new Integer(0));
		t.oscUtil.simpleOscMessage("/bird"+index+"/trig",v1,0);
	}
	//LogWindow.log("time("+pit+":"+(System.nanoTime()-start),"warn");
